```python
# file: services/kernel_manager.py
import docker
import jupyter_client
import json

class KernelManager:
    def __init__(self):
        self.client = docker.from_env()
        self.kernels = {}

    def get_or_create_kernel(self, user_id: str):
        container_name = f"chonost-kernel-{user_id}"

        try:
            container = self.client.containers.get(container_name)
        except docker.errors.NotFound:
            # ‡∏™‡∏£‡πâ‡∏≤‡∏á container ‡πÉ‡∏´‡∏°‡πà
            container = self.client.containers.run(
                "chonost-kernel:latest",
                detach=True,
                name=container_name,
                ports={'8888/tcp': None}
            )

        # ‡∏î‡∏∂‡∏á port ‡∏ó‡∏µ‡πà map
        port = container.ports['8888/tcp'][0]['HostPort']

        # ‡∏™‡∏£‡πâ‡∏≤‡∏á kernel connection
        kernel_info = {
            'container': container,
            'port': port,
            'connection_file': f"/tmp/kernel-{user_id}.json"
        }

        self.kernels[user_id] = kernel_info
        return kernel_info

    def execute_code(self, user_id: str, code: str):
        kernel_info = self.get_or_create_kernel(user_id)

        # ‡∏™‡∏£‡πâ‡∏≤‡∏á kernel client
        km = jupyter_client.KernelManager(
            connection_file=kernel_info['connection_file']
        )
        km.start_kernel()

        # ‡∏£‡∏±‡∏ô‡πÇ‡∏Ñ‡πâ‡∏î
        kc = km.client()
        msg_id = kc.execute(code)

        # ‡∏£‡∏≠‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
        results = []
        while True:
            try:
                msg = kc.get_shell_msg(timeout=1)
                if msg['parent_header']['msg_id'] == msg_id:
                    break
            except:
                break

        # ‡∏î‡∏∂‡∏á‡∏ú‡∏•‡∏•‡∏±‡∏û‡∏ò‡πå
        while True:
            try:
                msg = kc.get_iopub_msg(timeout=1)
                if msg['parent_header']['msg_id'] == msg_id:
                    content = msg['content']
                    if content.get('execution_state') == 'idle':
                        break
                    if 'text' in content:
                        results.append({
                            'type': 'stdout',
                            'content': content['text']
                        })
                    if 'data' in content:
                        if 'image/png' in content['data']:
                            results.append({
                                'type': 'image',
                                'content': content['data']['image/png']
                            })
            except:
                break

        return results
```

