from qdrant_client import QdrantClient
from sentence_transformers import SentenceTransformer
import litellm

class RAGService:
    def __init__(self):
        self.embedding_model = SentenceTransformer('nomic-ai/nomic-embed-text-v1.5')
        self.qdrant_client = QdrantClient(host="localhost", port=6333)

    def create_collection(self, collection_name: str):
        """‡∏™‡∏£‡πâ‡∏≤‡∏á collection ‡πÉ‡∏´‡∏°‡πà‡πÉ‡∏ô Qdrant"""
        try:
            self.qdrant_client.create_collection(
                collection_name=collection_name,
                vectors_config={
                    "size": 768,  # ‡∏Ç‡∏ô‡∏≤‡∏î‡∏Ç‡∏≠‡∏á embedding
                    "distance": "Cosine"
                }
            )
            return True
        except Exception as e:
            print(f"Error creating collection: {e}")
            return False

    def add_documents(self, collection_name: str, documents: list):
        """‡πÄ‡∏û‡∏¥‡πà‡∏°‡πÄ‡∏≠‡∏Å‡∏™‡∏≤‡∏£‡∏•‡∏á‡πÉ‡∏ô vector database"""
        try:
            for i, doc in enumerate(documents):
                # ‡∏™‡∏£‡πâ‡∏≤‡∏á embedding
                embedding = self.embedding_model.encode(doc['text']).tolist()

                # ‡πÄ‡∏û‡∏¥‡πà‡∏°‡∏•‡∏á‡πÉ‡∏ô Qdrant
                self.qdrant_client.upsert(
                    collection_name=collection_name,
                    points=[{
                        'id': i,
                        'vector': embedding,
                        'payload': {
                            'text': doc['text'],
                            'title': doc.get('title', ''),
                            'source': doc.get('source', '')
                        }
                    }]
                )
            return True
        except Exception as e:
            print(f"Error adding documents: {e}")
            return False

    def search_context(self, query: str, collection_name: str, limit: int = 5):
        """‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡∏ö‡∏£‡∏¥‡∏ö‡∏ó‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á"""
        try:
            # ‡∏™‡∏£‡πâ‡∏≤‡∏á embedding ‡∏Ç‡∏≠‡∏á query
            query_vector = self.embedding_model.encode(query).tolist()

            # ‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÉ‡∏ô Qdrant
            search_result = self.qdrant_client.search(
                collection_name=collection_name,
                query_vector=query_vector,
                limit=limit
            )

            # ‡∏™‡πà‡∏á‡∏Å‡∏•‡∏±‡∏ö‡∏Ç‡πâ‡∏≠‡∏Ñ‡∏ß‡∏≤‡∏°‡∏ó‡∏µ‡πà‡πÄ‡∏Å‡∏µ‡πà‡∏¢‡∏ß‡∏Ç‡πâ‡∏≠‡∏á
            return [hit.payload['text'] for hit in search_result]

        except Exception as e:
            print(f"Error searching context: {e}")
            return []

    def answer_with_context(self, query: str, context: list[str]):
        """‡∏ï‡∏≠‡∏ö‡∏Ñ‡∏≥‡∏ñ‡∏≤‡∏°‡πÇ‡∏î‡∏¢‡πÉ‡∏ä‡πâ‡∏ö‡∏£‡∏¥‡∏ö‡∏ó‡∏ó‡∏µ‡πà‡∏Ñ‡πâ‡∏ô‡∏´‡∏≤‡πÑ‡∏î‡πâ"""
