    )

def pytest_collection_modifyitems(config, items):
    """Modify test collection."""
    for item in items:
        # Add markers based on test file names
        if "test_performance" in item.nodeid:
            item.add_marker(pytest.mark.performance)
        elif "test_api" in item.nodeid:
            item.add_marker(pytest.mark.api)
        elif "test_integration" in item.nodeid:
            item.add_marker(pytest.mark.integration)
        else:
            item.add_marker(pytest.mark.unit)

# Test utilities
class TestUtils:
    """Utility functions for testing."""

    @staticmethod
    def create_mock_response(content: str, model: str = "test-model", 
                           cost: float = 0.001, tokens: int = 50):
        """Create a mock AI response."""
        return Mock(
            content=content,
            model_used=model,
            cost_estimate=cost,
            tokens_used=tokens
        )

    @staticmethod
    def create_mock_forecast_response(current: float = 0.5, predicted: float = 0.8,
                                    confidence: str = "high", timeframe: str = "next_week"):
        """Create a mock forecast response."""
        return Mock(
            content=json.dumps({
                "current_value": current,
                "predicted_value": predicted,
                "confidence": confidence,
                "timeframe": timeframe,
                "factors": ["test_factor"]
            })
        )

    @staticmethod
    def assert_response_structure(response_data: Dict[str, Any], required_fields: List[str]):
        """Assert that response has required fields."""
        for field in required_fields:
            assert field in response_data, f"Missing required field: {field}"

    @staticmethod
    def assert_performance_threshold(duration: float, max_duration: float, operation: str):
        """Assert performance threshold."""
        assert duration < max_duration, f"{operation} took too long: {duration:.2f} seconds"

    @staticmethod
    def create_test_data(record_count: int = 10) -> List[Dict[str, Any]]:
        """Create test data records."""
        return [
            {
                "id": f"test_{i}",
                "latency_ms": 1000 + (i * 100),
                "cost_estimate": 0.001 + (i * 0.0001),
                "successful": i % 2 == 0,
                "timestamp": datetime.now().isoformat()
            }
            for i in range(record_count)
        ]

# Make TestUtils available as a fixture
@pytest.fixture(scope="session")
def test_utils():
    """Provide test utilities."""
    return TestUtils
